        <script>
            // Tunggu hingga DOM selesai dimuat
            document.addEventListener("DOMContentLoaded", function() {
                const dropdownItems = document.querySelectorAll('.dropdown-item'); // Ambil semua opsi dropdown
                const table = document.getElementById('table1'); // Ambil tabel

                // Tambahkan event listener untuk setiap item dropdown
                dropdownItems.forEach(item => {
                    item.addEventListener('click', function() {
                        const filter = this.textContent
                            .toUpperCase(); // Ambil teks filter dari dropdown
                        const rows = table.getElementsByTagName('tr'); // Ambil semua baris tabel

                        // Iterasi melalui semua baris kecuali header
                        for (let i = 1; i < rows.length; i++) {
                            const statusCell = rows[i].getElementsByTagName('td')[
                                5]; // Ambil kolom ke-6 (Status)

                            if (statusCell) {
                                const statusText = statusCell.innerText.toUpperCase();

                                // Tampilkan/Sembunyikan baris berdasarkan filter
                                if (filter === "SEMUA STATUS" || statusText.includes(filter)) {
                                    rows[i].style.display = '';
                                } else {
                                    rows[i].style.display = 'none';
                                }
                            }
                        }
                    });
                });
            });
        </script>
        <script>
            // Tunggu hingga DOM selesai dimuat
            document.addEventListener("DOMContentLoaded", function() {
                // Ambil elemen input pencarian dan tabel
                const searchInput = document.getElementById('searchInput');
                const table = document.getElementById('table1');

                // Tambahkan event listener untuk pencarian
                searchInput.addEventListener('keyup', function() {
                    const filter = searchInput.value.toUpperCase(); // Konversi input menjadi huruf besar
                    const rows = table.getElementsByTagName('tr'); // Dapatkan semua baris tabel

                    // Iterasi melalui semua baris kecuali header
                    for (let i = 1; i < rows.length; i++) {
                        let nameCell = rows[i].getElementsByTagName('td')[0]; // Ambil kolom pertama (Nama)

                        if (nameCell) {
                            // Cocokkan nilai pencarian dengan kolom Nama
                            const nameText = nameCell.innerText.toUpperCase();
                            rows[i].style.display = nameText.includes(filter) ? '' : 'none';
                        }
                    }
                });
            });
        </script>
        <script>
            $(document).ready(function() {
                $('.update-status').click(function() {
                    var button = $(this);
                    var pengajuanId = button.data('id');
                    var statusBaru = button.data('status');

                    $.ajax({
                        url: "{{ route('pengajuan.updateStatus') }}",
                        type: "POST",
                        data: {
                            id: pengajuanId,
                            status: statusBaru,
                            _token: "{{ csrf_token() }}"
                        },
                        success: function(response) {
                            if (response.success) {
                                button.prop('disabled', true);
                                button.siblings('.update-status').prop('disabled', true);
                                alert('Status berhasil diperbarui');
                                location.reload();
                            } else {
                                alert('Gagal memperbarui status');
                            }
                        }
                    });
                });
            });
        </script>
</body>

